    };
    // this.handleChange = this.handleChange.bind(this);
  }

  render() {
    this.setState(st => {
      compCol: st.this.props.stateCol;
    });
    return (
      //   <div>
      <div
        onClick={this.props.func}
        className="ColorBox"
        // onClick={this.handleChange}
        style={{ backgroundColor: `${this.state.compCol}` }}
