import React, { Component } from 'react';
import ColorComp from './ColorComp';

class RandomColor extends Component {
  static defaultProps = {
    colorOption: [
      'blue',
      'brown',
      'teal',
      'aliceblue',
      'aqua',
      'darkcyan',
      'darkslategray',
      'slategray',
      'lightslategrey'
    ]
  };
  constructor(props) {
    super(props);
    this.state = {
      myColor: []
    };
  }

  render() {
    return (
      <div>
        <h1>Random Color Component</h1>
        {this.props.colorOption.map((c, idx) => (
          <ColorComp func={this.handleClick} key={idx} boxCol={c} track={idx} />
        ))}
      </div>
    );
  }
}

export default RandomColor;
